function abreOuFechaResposta(e){e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function hideAllTabs(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}function removeActiveButton(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function hiddenHeader(){document.querySelector("header").classList.add("header--is-hidden")}function showHeader(){document.querySelector("header").classList.remove("header--is-hidden")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),a=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?hiddenHeader:showHeader)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id="${t}"]`);hideAllTabs(),t.classList.add("shows__list--is-active"),removeActiveButton(),e.target.classList.add("shows__tabs__button--is-active")});for(let e=0;e<a.length;e++)a[e].addEventListener("click",abreOuFechaResposta)});